                    data-testid="input-search"
                  />
                  <Button 
                    onClick={handleSearch}
                    className="bg-blue-600 hover:bg-blue-700 text-white"
                    data-testid="button-search"
                  >
                    Search
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Students Table */}
          <StudentTable 
            students={students as Student[] || []} 
            isLoading={studentsLoading} 
          />
        </div>
      </main>
    </div>
  );
}

